import React from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import {Input, Button} from '@material-ui/core';

class AirportForm extends React.Component {
  handleSubmit = (data) => {
    console.log('data: ', data)
    this.props.onSubmit(data);
  }

  handleCancel = () => {
    this.props.onCancel();
  }
  render() {
    return (
      <Formik
       initialValues={{ email: '', password: '' }}
       validate={values => {
         const errors = {};
         if (!values.email) {
           errors.email = 'Required';
         } else if (
           !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
         ) {
           errors.email = 'Invalid email address';
         }
         return errors;
       }}
       onSubmit={(values, {setSubmitting}) => {
        this.handleSubmit(values);
      }}
       >
       {({ isSubmitting }) => (
         <Form>
          <Input placeholder='Name' name='name'/><br/>
          <Input placeholder='Country' name='country'/><br/>
          <Input placeholder='Location' name='location'/><br/>
          <Input placeholder='Departing from' name='departingFrom'/><br/>
          <Input placeholder='Landing to' name='landingTo'/><br/>
          <Button type='submit' color='primary' variant='contained'>Submit</Button>
          <Button variant='contained' onClick={this.handleCancel}>Cancel</Button>
         </Form>
       )}
     </Formik>
    )
  }
}

export default AirportForm;
