import * as Actions from 'redux/actions/flights.js'

//reducer
const flightsReducer = (state = {}, action) => {
  switch (action.type) {
    case Actions.FLIGHTS_FETCH: {
      return {
        ...state,
        flights: [...action.flights],
      }
    }

    case Actions.FLIGHT_FETCH: {
      return {
        ...state,
        flight: action.flights,
      }
    }

    default:
      return state;
  }
};

export default flightsReducer;
