//actions
const AIRPORTS_FETCH = 'AIRPORTS_FETCH';
const AIRPORT_FETCH = 'AIRPORT_FETCH';
const AIRPORT_CREATE = 'AIRPORT_CREATE';
const AIRPORT_UPDATE = 'AIRPORT_UPDATE';
const AIRPORT_DELETE = 'AIRPORT_DELETE';

//effects
export const fetchAirports = payload => {
  return dispatch => {
    fetch('http://localhost:3001/airports').then(res =>
      res.json()
      ).then(airports => {
        console.log(airports);
      dispatch({
        type: AIRPORTS_FETCH,
        airports,
      });
    });
  };
};

export const fetchAirportByIdEffect = (id) => {
  return dispatch => {
    fetch(`http://localhost:3001/airport/${id}`).then(airport => {
      console.log('airport: ', airport)
      dispatch({
        type: AIRPORT_FETCH,
        airports,
      });
    });
  };
};

export const createAirportEffect = (payload) => {
  console.log('payload: ', payload);
  const reqOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }

  return dispatch => {
    fetch('http://localhost:3001/airports', reqOptions)
    .then(res => res.json())
    .then(data => console.log('res data: ', data)
    )
  }
}
export const updateAirportEffect = (payload) => {
  axios.put().then()
}
export const deleteAirportEffect = (payload) => {
  axios.delete().then()
}

//reducer
const airportsReducer = (state = {airports: []}, action) => {
  console.log('state: ', state);
  console.log('action: ', action.airports);

  return state;
};

export default airportsReducer;
