//reducer
const airportsReducer = (state = {}, action) => {
  switch (action.type) {
    case AIRPORTS_FETCH: {
      return {
        ...state,
        airports: [...action.airports],
      }
    }

    case AIRPORT_FETCH: {
      return {
        ...state,
        airport: action.airports,
      }
    }

    case AIRPORT_UPDATE: {
      return [
        ...state.filter(airport => airport.id !== action.airport.id),
        Object.assign({}, action.airport)
      ]
    }

    default:
      return state;
  }
};

export default airportsReducer;
