import React from 'react';
import {connect} from 'react-redux';
import {Modal, Button} from '@material-ui/core';
import {
  fetchAirlines,
  createAirline,
  updateAirline,
  deleteAirline,
} from 'redux/reducers/airlines.js';
import AirlineList from 'components/airlines/list/AirlineList';
import ComponentListHeader from 'components/common/ComponentListHeader';
import AirlineForm from 'components/airlines/form/AirlineForm';
import 'assets/style/modalView.css';

class AirlineListContainer extends React.Component {
  constructor (props) {
    super (props);

    this.state = {
      modalVisible: false,
      body: '',
    };
  }

  componentDidMount() {
    this.fetchAirlines();
  }

  componentDidUpdate (prevProps, prevState) {
    if (prevProps.airlines !== this.props.airlines) {
      this.fetchAirlines();
    }
  }

  fetchAirlines = () => {
    this.props.fetchAirlines();
  };

  handleCreateAirline = data => {
    this.props.createAirline (data);
    this.handleCloseModal();
  };

  handleUpdateAirline = data => {
    this.props.updateAirline(data);
    this.handleCloseModal();
  }

  handleDeleteAirline = id => {
    this.props.deleteAirline (id);
    this.handleCloseModal();
  };

  handleOpenCreateModal = () => {
    const body = (
      <AirlineForm
        onSubmit={this.handleCreateAirline}
        onCancel={this.handleCloseModal}
      />
    );

    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleEditClick = (id) => {
    const airlineToUpdate = this.props.airlines.find(airline => parseInt(airline.id) === parseInt(id));
    const body = (
      <AirlineForm
        airline={airlineToUpdate}
        onSubmit={this.handleUpdateAirline}
        onCancel={this.handleCloseModal}
      />
    );
    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleDeleteClick = id => {
    const body = (
      <React.Fragment>
        <p>Are you sure you want to delete this airline?</p>
        <Button
          color="danger"
          variant="contained"
          onClick={() => this.handleDeleteAirline (id)}
        >
          Confirm
        </Button>
        <Button onClick={this.handleCloseModal}>Cancel</Button>
      </React.Fragment>
    );
    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleCloseModal = () => {
    this.setState({
      modalVisible: false,
      body: '',
    });
  };

  render() {
    const {airlines} = this.props;
    const {modalVisible, body} = this.state;
    return (
      <React.Fragment>
        <ComponentListHeader onOpenCreateModal={this.handleOpenCreateModal} />
        <br />
        {airlines && airlines.length > 0
          ? <AirlineList
              airlines={airlines || []}
              onEditClick={this.handleEditClick}
              onDeleteClick={this.handleDeleteClick}
            />
          : 'No data to display'}
        {modalVisible &&
          <Modal
            className="twoesys-modalView__modal"
            open={true}
            onClose={this.handleCloseModal}
          >
            {body}
          </Modal>}
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
  airlines: state.airlinesReducer.airlines,
});

const mapDispatchToProps = dispatch => {
  return {
    fetchAirlines: () => dispatch (fetchAirlines()),
    createAirline: data => dispatch (createAirline(data)),
    updateAirline: data => dispatch (updateAirline(data)),
    deleteAirline: id => dispatch (deleteAirline(id)),
  };
};

export default connect (mapStateToProps, mapDispatchToProps)(AirlineListContainer);
