import React from 'react';
import {connect} from 'react-redux';
import { Field, reduxForm } from 'redux-form'
import {Modal, Button} from '@material-ui/core';
import {fetchAirports} from 'redux/reducers/airports.js';
import AirportList from 'components/airports/list/AirportList';
import AirportListHeader from 'components/airports/view/AirportListHeader';

class AirportListContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      modalVisible: false
    }
  }
  componentDidMount() {
    this.fetchAirports();
  }

  fetchAirports = () => {
    this.props.fetchAirports();
  }

  createAirport = () => {

  }

  deleteAirport = () => {

  }

  handleToggleCreateModal = () => {
    const body = (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label>First Name</label>
          <div>
            <Field
              name="firstName"
              component="input"
              type="text"
              placeholder="First Name"
            />
          </div>
        </div>
        <div>
          <label>Last Name</label>
          <div>
            <Field
              name="lastName"
              component="input"
              type="text"
              placeholder="Last Name"
            />
          </div>
        </div>
        <div>
          <label>Email</label>
          <div>
            <Field
              name="email"
              component="input"
              type="email"
              placeholder="Email"
            />
          </div>
        </div>
        <div>
          <label>Sex</label>
          <div>
            <label>
              <Field name="sex" component="input" type="radio" value="male" />{' '}
              Male
            </label>
            <label>
              <Field name="sex" component="input" type="radio" value="female" />{' '}
              Female
            </label>
            <label>
              <Field name="sex" component="input" type="radio" value="other" />{' '}
              Other
            </label>
          </div>
        </div>
        <div>
          <label>Favorite Color</label>
          <div>
            <Field name="favoriteColor" component="select">
              <option />
              <option value="ff0000">Red</option>
              <option value="00ff00">Green</option>
              <option value="0000ff">Blue</option>
            </Field>
          </div>
        </div>
        <div>
          <label htmlFor="employed">Employed</label>
          <div>
            <Field
              name="employed"
              id="employed"
              component="input"
              type="checkbox"
            />
          </div>
        </div>
        <div>
          <label>Notes</label>
          <div>
            <Field name="notes" component="textarea" />
          </div>
        </div>
        <div>
          <Button type="submit">
            Submit
          </Button>
        </div>
      </form>
    );

    this.setState({
      modalVisible: !this.state.modalVisible,
      body,
    });
  }

  render() {
    const {airports} = this.props;
    const {modalVisible, body} = this.state;
    return (
      <React.Fragment>
        <AirportListHeader onToggleCreateModal={this.handleToggleCreateModal} />
        <br />
        {airports && airports.length > 0 ? <AirportList airports={airports || []} /> : 'No data to display'}
        {modalVisible && <Modal open={true}>{body}</Modal>}
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  airports: state.airports,
});

const mapDispatchToProps = (dispatch) => {
  return {
    fetchAirports: (payload) => dispatch(fetchAirports({test: 'test'})),
  }  
}

export default connect(mapStateToProps, mapDispatchToProps)(AirportListContainer);
