//actions
const AIRPORTS_FETCH = 'AIRPORTS_FETCH';
const AIRPORT_FETCH = 'AIRPORT_FETCH';
const AIRPORT_CREATE = 'AIRPORT_CREATE';
const AIRPORT_UPDATE = 'AIRPORT_UPDATE';
const AIRPORT_DELETE = 'AIRPORT_DELETE';

export const fetchAirports = (payload) => {
  console.log('in effect __ fetchAirports: ', payload);
  fetchAirportsEffect({
    type: AIRPORTS_FETCH,
    ...payload
  });
}

export const fetchAirportById = (payload) => {
  console.log('in effect __ fetchAirportById: ', payload);
  this.fetchAirportByIdEffect({
    type: AIRPORT_FETCH,
    payload,
  });
}

export const createAirport = (payload) => {
  console.log('in effect __ createAirport: ', payload);
  return {
    type: AIRPORT_CREATE,
    payload,
  }
}

export const updateAirport = (payload) => {
