  return {
    type: AIRPORT_UPDATE,
    payload,
  }
}

export const deleteAirport = (payload) => {
  console.log('in effect __ deleteAirport: ', payload);
  return {
    type: AIRPORT_DELETE,
    payload,
  }
}

