import React from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik';
import {TextField, Button} from '@material-ui/core';
import GoogleMapReact from 'google-map-react';
import RoomIcon from '@material-ui/icons/Room';

class FlightForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mapZoom: 12,
      draggable: true,
      lat: this.props.flight === undefined ? 45.746686123840526 : parseFloat(this.props.flight.location.split(', ')[0]),
      lng: this.props.flight === undefined ? 16.045964602812305 : parseFloat(this.props.flight.location.split(', ')[1]),
    };

    this.googleMaps = null;
  }

  handleSubmit = data => {
    data.location = `${this.state.lat}, ${this.state.lng}`
    delete data.departingFrom;
    delete data.landingTo;
    if (this.props.flight && this.props.flight.id) {
      data.id = this.props.flight.id;
    }

    this.props.onSubmit(data);
  };

  handleCancel = () => {
    this.props.onCancel();
  };

  mapLoaded = (map) => {
    this.googleMaps = map;
  };

  onMarkerMove = (key, currentValues, newValues) => {
    this.setState({
      draggable: false,
    });
    this.changeLatitude(null, newValues.lat);
    this.changeLongitude(null, newValues.lng);
  };

  onMarkerDown = () => {
    this.setState({
      draggable: true,
    });
  };

  changeLatitude = (e, val) => {
    this.setState({
      lat: val,
    });
  };

  changeLongitude = (e, val) => {
    this.setState({
      lng: val,
    });
  };

  render () {
    const {flight, countries} = this.props;

    const marker = {
      id: flight === undefined ? null : flight.id,
      size: { width: 62, height: 60 },
      origin: { x: 15 / 62, y: 1 },
      withText: false,
    };

    return (
      <Formik
        initialValues={{
          name: flight && flight.name ? flight.name : '',
          country: flight && flight.country ? flight.country : '',
          departingFrom: flight && flight.departingFrom ? flight.departingFrom : '',
          landingTo: flight && flight.landingTo ? flight.landingTo : '',
        }}
        onSubmit={(values, {setSubmitting}) => {
          this.handleSubmit(values);
        }}
      >
        {({isSubmitting}) => (
          <Form>
            <label htmlFor="name">Name</label>
            <Field id="name" name="name" placeholder="Name" />
            <br />
            <label htmlFor="country">Country</label>
            <Field id="country" name="country" placeholder="Country" as='select'>
              {countries.map(country => (<option key={country.id} value={country.id}>{country.name}</option>))}
            </Field>
            <br />
            <label htmlFor="departingFrom">Departing from</label>
            <Field
              id="departingFrom"
              name="departingFrom"
              placeholder="Departing from"
            />
            <br />
            <label htmlFor="landingTo">Landing to</label>
            <Field id="landingTo" name="landingTo" placeholder="Landing to" />
            <br />
            <label htmlFor="location">Location</label>
              <div style={{ height: 300, width: 500 }}>
                <GoogleMapReact
                  bootstrapURLKeys={{key: 'AIzaSyCE_hqyzF4Zm4T3py8ieljazaeyiwa678A'}}
                  name="map"
                  defaultZoom={10}
                  center={{
                    lat: flight === undefined ? 45.746686123840526 : parseFloat(flight.location.split(', ')[0]),
                    lng: flight === undefined ? 16.045964602812305 : parseFloat(flight.location.split(', ')[1]),
                  }}
                  onChildMouseUp={this.onMarkerDown}
                  onChildMouseMove={this.onMarkerMove}
                  onGoogleApiLoaded={this.mapLoaded}
                  yesIWantToUseGoogleMapApiInternals
                  draggable={this.state.draggable}
                >
                  <RoomIcon
                    key={flight === undefined ? null : flight.id}
                    marker={marker}
                    lat={this.state.lat}
                    lng={this.state.lng}
                  />
                </GoogleMapReact>
              </div>
            <br />
            <Button type="submit" color="primary" variant="contained">
              Submit
            </Button>
            <Button variant="contained" onClick={this.handleCancel}>
              Cancel
            </Button>
          </Form>
        )}
      </Formik>
    );
  }
}

export default FlightForm;
