import React from 'react';
import {connect} from 'react-redux';
import {updateFlight, fetchFlightById} from 'redux/actions/flights.js';
import {fetchCountries} from "redux/actions/countries.js";
import FlightForm from 'components/flights/form/FlightForm';

class FlightUpdateContainer extends React.Component {
  componentDidMount () {
    this.fetchCountries();
    if (this.props.flightId) {
      this.fetchFlightById(this.props.flightId);
    }
  }

  fetchFlightById = id => {
    this.props.fetchFlightById(id);
  }

  fetchCountries = () => {
    this.props.fetchCountries();
  }

  handleUpdateFlight = data => {
    this.props.updateFlight(data);
    this.handleCloseModal();
  };

  handleCloseModal = () => {
    this.props.onCancel();
  };

  render() {
    const {flight, countries} = this.props;
    return (
      <React.Fragment>
        {flight &&
          <FlightForm
            flight={flight}
            countries={countries}
            onSubmit={this.handleUpdateFlight}
            onCancel={this.handleCloseModal}
          />}
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
  flight: state.flightsReducer.flight,
  countries: state.countriesReducer.countries,
});

const mapDispatchToProps = dispatch => {
  return {
    fetchFlightById: id => dispatch (fetchFlightById (id)),
    updateFlight: data => dispatch (updateFlight(data.id, data)),
    fetchCountries: () => dispatch(fetchCountries()),
  };
};

export default connect (mapStateToProps, mapDispatchToProps) (
  FlightUpdateContainer
);
