import {TextField, Button} from '@material-ui/core';

class AirportForm extends React.Component {
  handleSubmit = (data) => {
    console.log('data: ', data)
    this.props.onSubmit(data);
  }

  handleCancel = () => {
    this.props.onCancel();
  }
  render() {
    return (
      <Formik
       initialValues={{ 
        name: '',
        country: '',
        location: '',
        departingFrom: '',
        landingTo: ''}}
       onSubmit={(values, {setSubmitting}) => {
         console.log(values)
        this.handleSubmit(values);
      }}
       >
       {({ isSubmitting, values }) => (
         <Form>
