//actions
const COUNTRIES_FETCH = 'COUNTRIES_FETCH';
const COUNTRY_FETCH = 'COUNTRY_FETCH';
const COUNTRY_CREATE = 'COUNTRY_CREATE';
const COUNTRY_UPDATE = 'COUNTRY_UPDATE';
const COUNTRY_DELETE = 'COUNTRY_DELETE';

//effects
export const fetchCountries = payload => {
  return dispatch => {
    fetch('http://localhost:3001/countries').then(res =>
      res.json()
      ).then(countries => {
      dispatch({
        type: COUNTRIES_FETCH,
        countries: [...countries],
      });
    });
  };
};

export const fetchCountryById = (id) => {
  return dispatch => {
    fetch(`http://localhost:3001/country/${id}`)
    .then(res => res.json())
    .then(country => {
      dispatch({
        type: COUNTRY_FETCH,
        country,
      });
    });
  };
};

export const createCountry = (payload) => {
  const reqOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }

  return dispatch => {
    fetch('http://localhost:3001/countries', reqOptions)
    .then(res => res.json())
    .then(data => {
      dispatch({
        type: COUNTRY_CREATE,
        data
      });
      }
    )
  }
}
export const updateCountry = (payload) => {
  const reqOptions = {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }

  return dispatch => {
    fetch(`http://localhost:3001/country/${payload.id}`, reqOptions)
    .then(res => res.json())
    .then(data => {
      dispatch({
        type: COUNTRY_UPDATE,
        data
      });
      }
    )
  }
}

export const deleteCountry = (id) => {
  return dispatch => {
    fetch(`http://localhost:3001/country/${id}`, { method: 'DELETE' })
    .then((res) =>{
      dispatch({
        type: COUNTRY_DELETE,
      });
      }
    )
  }
}

//reducer
const countriesReducer = (state = {}, action) => {
  switch (action.type) {
    case COUNTRIES_FETCH: {
      return {
        ...state,
        countries: [...action.countries],
      }
    }

    case COUNTRY_FETCH: {
      return {
        ...state,
        country: action.country[0],
      }
    }

    default:
      return state;
  }
};

export default countriesReducer;
