//actions
const AIRPORTS_FETCH = 'AIRPORTS_FETCH';
const AIRPORT_FETCH = 'AIRPORT_FETCH';
const AIRPORT_CREATE = 'AIRPORT_CREATE';
const AIRPORT_UPDATE = 'AIRPORT_UPDATE';
const AIRPORT_DELETE = 'AIRPORT_DELETE';

//effects
export const fetchAirports = payload => {
  return dispatch => {
    fetch('http://localhost:3001/airports').then(res =>
      res.json()
      ).then(airports => {
      dispatch({
        type: AIRPORTS_FETCH,
        airports: [...airports],
      });
    });
  };
};

export const fetchAirportById = (id) => {
  return dispatch => {
    fetch(`http://localhost:3001/airport/${id}`).then(airport => {
      console.log('airport: ', airport)
      dispatch({
        type: AIRPORT_FETCH,
        airports,
      });
    });
  };
};

export const createAirport = (payload) => {
  console.log('payload: ', payload);
  const reqOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }

  return dispatch => {
    fetch('http://localhost:3001/airports', reqOptions)
    .then(res => res.json())
    .then(data => {
      dispatch({
        type: AIRPORT_CREATE,
        data
      });
      fetchAirports();
      }
    )
  }
}
export const updateAirport = (payload) => {
  //axios.put().then()
}

export const deleteAirport = (id) => {
  fetch(`http://localhost:3001/airport/${id}`, { method: 'DELETE' })
  .then(() => fetchAirports());
}

//reducer
const airportsReducer = (state = {airports: []}, action) => {
  switch (action.type) {
    case AIRPORTS_FETCH: {
      return {
        ...state,
        airports: [...action.airports],
      }
    }

    case AIRPORT_DELETE: {
      console.log('state: ', state)
      console.log('action: ', action)
    }

    default:
      return state;
  }
};

export default airportsReducer;
