import React, {forwardRef} from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik';
import {TextField, Button} from '@material-ui/core';

class AirlineForm extends React.Component {
  handleSubmit = data => {
    if (this.props.airline && this.props.airline.id) {
      data.id = this.props.airline.id;
    }

    this.props.onSubmit(data);
  };

  handleCancel = () => {
    this.props.onCancel();
  };
  render () {
    const {airline} = this.props;
    return (
      <Formik
        initialValues={{
          name: airline && airline.name ? airline.name : '',
          country: airline && airline.country ? airline.country : '',
        }}
        onSubmit={(values, {setSubmitting}) => {
          this.handleSubmit(values);
        }}
      >
        {({isSubmitting}) => (
          <Form>
            <label htmlFor="name">Name</label>
            <Field id="name" name="name" placeholder="Name" />
            <br />
            <label htmlFor="country">Country</label>
            <Field id="country" name="country" placeholder="Country" />
            <br />
            <Button type="submit" color="primary" variant="contained">
              Submit
            </Button>
            <Button variant="contained" onClick={this.handleCancel}>
              Cancel
            </Button>
          </Form>
        )}
      </Formik>
    );
  }
}

export default AirlineForm;
