import React from 'react';
import {connect} from 'react-redux';
import {createFlight, fetchFlights} from 'redux/actions/flights.js';
import {fetchCountries} from "redux/actions/countries.js";
import FlightForm from 'components/flights/form/FlightForm';

class FlightCreateContainer extends React.Component {
  componentDidMount() {
    this.fetchCountries();
  }

  handleCreateFlight = data => {
    this.props.createFlight(data);
    this.handleCloseModal();
  };

  handleCloseModal = () => {
    this.props.onCancel();
  }

  fetchCountries = () => {
    this.props.fetchCountries();
  }

  render() {
    const {countries, flights} = this.props;
    return (
      <React.Fragment>
        {countries && <FlightForm
          countries={countries}
          onSubmit={this.handleCreateFlight}
          onCancel={this.handleCloseModal}
        />}
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  countries: state.countriesReducer.countries,
  flights: state.flightsReducer.flights,
});

const mapDispatchToProps = dispatch => {
  return {
    fetchFlights: () => dispatch(fetchFlights()),
    createFlight: data => dispatch(createFlight(data)),
    fetchCountries: () => dispatch(fetchCountries()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(FlightCreateContainer);
