import React from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik';
import {TextField, Button} from '@material-ui/core';
import GoogleMapReact from 'google-map-react';
import RoomIcon from '@material-ui/icons/Room';

class AirportForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mapZoom: 12,
      draggable: true,
      lat: this.props.airport === undefined ? 45.746686123840526 : parseFloat(this.props.airport.location.split(', ')[0]),
      lng: this.props.airport === undefined ? 16.045964602812305 : parseFloat(this.props.airport.location.split(', ')[1]),
    };

    this.googleMaps = null;
  }

  handleSubmit = data => {
    data.location = `${this.state.lat}, ${this.state.lng}`

    if (this.props.airport && this.props.airport.id) {
      data.id = this.props.airport.id;
    }
    console.log('data: ', data)

    this.props.onSubmit(data);
  };

  handleCancel = () => {
    this.props.onCancel();
  };

  mapLoaded = (map) => {
    this.googleMaps = map;
  };

  onMarkerMove = (key, currentValues, newValues) => {
    this.setState({
      draggable: false,
    });
    this.changeLatitude(null, newValues.lat);
    this.changeLongitude(null, newValues.lng);
  };

  onMarkerDown = () => {
    this.setState({
      draggable: true,
    });
  };

  changeLatitude = (e, val) => {
    this.setState({
      lat: val,
    });
  };

  changeLongitude = (e, val) => {
    this.setState({
      lng: val,
    });
  };

  render () {
    const {airport, airports, countries} = this.props;

    let availableAirports; 

    if (airports && airports.length) {
      availableAirports = airports.filter(availableAirport => airport.id !== availableAirport.id)
    } else {
      availableAirports = [];
    }

    const marker = {
      id: airport === undefined ? null : airport.id,
      size: { width: 62, height: 60 },
      origin: { x: 15 / 62, y: 1 },
      withText: false,
    };

    return (
      <Formik
        initialValues={{
          name: airport && airport.name ? airport.name : '',
          country: airport && airport.country ? airport.country : '',
          departingFrom: airport && airport.departingFrom ? airport.departingFrom : '',
          landingTo: airport && airport.landingTo ? airport.landingTo : '',
        }}
        onSubmit={(values, {setSubmitting}) => {
          console.log(values)
          //this.handleSubmit(values);
        }}
      >
        {({isSubmitting}) => (
          <Form>
            <label htmlFor="name">Name</label>
            <Field id="name" name="name" placeholder="Name" />
            <br />
            <label htmlFor="country">Country</label>
            <Field id="country" name="country" placeholder="Country" as='select'>
              {countries.map(country => (<option key={country.id} value={country.id}>{country.name}</option>)).splice(0, 0, <option selected="true" disabled="disabled">Choose Country</option>)}
            </Field>
            <br />
            <label htmlFor="departingFrom">Departing from</label>
            <Field
              id="departingFrom"
              name="departingFrom"
              placeholder="Departing from"
              as='select'
              disabled={availableAirports.length <= 0}
            >
              {availableAirports.map(airport => (
                <option key={airport.id} value={availableAirport.id}>{availableAirport.name}</option>
              )).splice(0, 0, <option selected="true" disabled="disabled">Choose departure</option>)}
            </Field>
            <br />
            <label htmlFor="landingTo">Landing to</label>
            <Field id="landingTo" name="landingTo" placeholder="Landing to" as='select' disabled={availableAirports.length <= 0}>
              {availableAirports.map(airport => (
                <option key={airport.id} value={availableAirport.id}>{availableAirport.name}</option>
              )).splice(0, 0, <option selected="true" disabled="disabled">Choose landing</option>)}
            </Field>
            <br />
            <label htmlFor="location">Location</label>
              <div style={{ height: 300, width: 500 }}>
                <GoogleMapReact
                  bootstrapURLKeys={{key: 'AIzaSyCE_hqyzF4Zm4T3py8ieljazaeyiwa678A'}}
                  name="map"
                  defaultZoom={10}
                  center={{
                    lat: airport === undefined ? 45.746686123840526 : parseFloat(airport.location.split(', ')[0]),
                    lng: airport === undefined ? 16.045964602812305 : parseFloat(airport.location.split(', ')[1]),
                  }}
                  onChildMouseUp={this.onMarkerDown}
                  onChildMouseMove={this.onMarkerMove}
                  onGoogleApiLoaded={this.mapLoaded}
                  yesIWantToUseGoogleMapApiInternals
                  draggable={this.state.draggable}
                >
                  <RoomIcon
                    key={airport === undefined ? null : airport.id}
                    marker={marker}
                    lat={this.state.lat}
                    lng={this.state.lng}
                  />
                </GoogleMapReact>
              </div>
            <br />
            <Button type="submit" color="primary" variant="contained">
              Submit
            </Button>
            <Button variant="contained" onClick={this.handleCancel}>
              Cancel
            </Button>
          </Form>
        )}
      </Formik>
    );
  }
}

export default AirportForm;
