import React from 'react';
import { connect } from 'react-redux';
import GoogleMapReact from 'google-map-react';

class MapContainer extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      mapCenter: null,
      mapZoom: 12,
      draggable: true,
      lat: this.props.airport === undefined ? 42.092424 : this.props.airport.latitude,
      lng: this.props.airport === undefined ? 11.795413 : this.props.airport.longitude,
    };

    this.googleMaps = null;
    this.defaultMapCenter = { lat: 42.092422, lng: 11.795413 };
  }

  mapLoaded = (map) => {
    this.googleMaps = map;
  };

  onMarkerMove = (key, currentValues, newValues) => {
    this.setState({
      draggable: false,
    });
    /*this.props.change('latitude', newValues.lat);
    this.props.change('longitude', newValues.lng);*/
    this.changeLatitude(null, newValues.lat);
    this.changeLongitude(null, newValues.lng);
  };

  onMarkerDown = () => {
    this.setState({
      draggable: true,
    });
  };

  changeLatitude = (e, val) => {
    this.setState({
      lat: val,
    });
  };

  changeLongitude = (e, val) => {
    this.setState({
      lng: val,
    });
  };

  render() {

    const marker = {
      id: this.props.airport === undefined ? null : this.props.airport.id,
      size: { width: 62, height: 60 },
      origin: { x: 15 / 62, y: 1 },
      withText: false,
    };

    let text = this.props.airport === undefined ? null : (<span>ID: {this.props.airport.id}</span>);

    return (
            <div style={{ height: 300, width: 500 }}>
              <GoogleMapReact
                bootstrapURLKeys={{key: 'AIzaSyCE_hqyzF4Zm4T3py8ieljazaeyiwa678A'}}
                name="map"
                defaultZoom={10}
                defaultCenter={{
                  lat: this.state.lat === 0 ? 42.092424 : this.state.lat,
                  lng: this.state.lng === 0 ? 11.795413 : this.state.lng,
                }}
                onChildMouseUp={this.onMarkerDown}
                onChildMouseMove={this.onMarkerMove}
                onGoogleApiLoaded={this.mapLoaded}
                yesIWantToUseGoogleMapApiInternals
                draggable={this.state.draggable}
              >
                <div
                  key={this.props.airport === undefined ? null : this.props.airport.id}
                  marker={marker}
                  text={text}
                  hoverable={false}
                  showBallon={false}
                  lat={this.state.lat}
                  lng={this.state.lng}
              >Marker</div>
              </GoogleMapReact>
            </div>
    );
  }
}

export default MapContainer;
