import React from 'react';
import {connect} from 'react-redux';
import {Modal} from '@material-ui/core';
import {fetchCountries} from 'redux/reducers/countries.js';
import CountryList from 'components/countries/list/CountryList';

class CountryListContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      modalVisible: false,
      body: '',
    }
  }

  componentDidMount() {
    this.fetchCountries();
  }

  componentDidUpdate(prevProps, prevState) {

  }

  fetchCountries = () => {
    this.props.fetchCountries();
  }

  createCountries = (data) => {
    this.props.createCountry(data);
    this.handleCloseModal();
  }

  handleCloseModal = () => {
    this.setState({
      modalVisible: false,
      body: '',
    });
  };

  render() {
    const {modalVisible, body} = this.state;
    const {countries} = this.props;
    return (
      <React.Fragment>
      {countries && countries.length > 0
          ? <CountryList
              countries={countries || []}
              onEditClick={this.handleEditClick}
              onDeleteClick={this.handleDeleteClick}
            />
          : 'No data to display'}
        {modalVisible && 
          <Modal open={true} onClose={this.handleCloseModal}>
            {body}
          </Modal>
        }
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  countries: state.countriesReducer.countries,
});

const mapDispatchToProps = (dispatch) => {
  return {
    fetchCountries: () => dispatch(fetchCountries()),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CountryListContainer);
