import React from 'react';
import {connect} from 'react-redux';
import {updateAirport, fetchAirportById} from 'redux/reducers/airports.js';
import AirportForm from 'components/airports/form/AirportForm';

class AirportUpdateContainer extends React.Component {
  componentDidMount () {
    if (this.props.id) {
      this.fetchAirportById(this.props.id);
    }
  }

  fetchAirportById = id => {
    this.props.fetchAirportById(id);
  }

  handleUpdateAirport = data => {
    this.props.updateAirport(data);
    this.handleCloseModal();
  };

  handleCloseModal = () => {
    this.props.onCancel();
  };

  render () {
    return (
      <React.Fragment>
        {this.props.airport &&
          <AirportForm
            airport={this.props.airport}
            onSubmit={this.handleUpdateAirport}
            onCancel={this.handleCloseModal}
          />}
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
  airport: state.airports.airport,
});

const mapDispatchToProps = dispatch => {
  return {
    fetchAirportById: id => dispatch (fetchAirportById (id)),
    updateAirport: data => dispatch (updateAirport (data)),
  };
};

export default connect (mapStateToProps, mapDispatchToProps) (
  AirportUpdateContainer
);
