//actions
const AIRLINES_FETCH = 'AIRLINES_FETCH';
const AIRLINE_FETCH = 'AIRLINE_FETCH';
const AIRLINE_CREATE = 'AIRLINE_CREATE';
const AIRLINE_UPDATE = 'AIRLINE_UPDATE';
const AIRLINE_DELETE = 'AIRLINE_DELETE';

//effects
export const fetchAirlines = payload => {
  return dispatch => {
    fetch('http://localhost:3001/airlines').then(res =>
      res.json()
      ).then(airlines => {
      dispatch({
        type: AIRLINES_FETCH,
        airlines: [...airlines],
      });
    });
  };
};

export const fetchAirlineById = (id) => {
  return dispatch => {
    fetch(`http://localhost:3001/airline/${id}`)
    .then(res => res.json())
    .then(airline => {
      dispatch({
        type: AIRLINE_FETCH,
        airline,
      });
    });
  };
};

export const createAirline = (payload) => {
  const reqOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }

  return dispatch => {
    fetch('http://localhost:3001/airlines', reqOptions)
    .then(res => res.json())
    .then(data => {
      dispatch({
        type: AIRLINE_CREATE,
        data
      });
      }
    )
  }
}
export const updateAirline = (payload) => {
  const reqOptions = {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }

  return dispatch => {
    fetch(`http://localhost:3001/airline/${payload.id}`, reqOptions)
    .then(res => res.json())
    .then(data => {
      dispatch({
        type: AIRLINE_UPDATE,
        data
      });
      }
    )
  }
}

export const deleteAirline = (id) => {
  return dispatch => {
    fetch(`http://localhost:3001/airline/${id}`, { method: 'DELETE' })
    .then((res) =>{
      dispatch({
        type: AIRLINE_DELETE,
      });
      }
    )
  }
}

//reducer
const airlinesReducer = (state = {}, action) => {
  switch (action.type) {
    case AIRLINES_FETCH: {
      return {
        ...state,
        airlines: [...action.airlines],
      }
    }

    case AIRLINE_FETCH: {
      return {
        ...state,
        airline: action.airline[0],
      }
    }

    default:
      return state;
  }
};

export default airlinesReducer;
