import React from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik';
import {TextField, Button} from '@material-ui/core';

class AirportForm extends React.Component {
  handleSubmit = data => {
    delete data.departingFrom;
    delete data.landingTo;
    if (this.props.airport && this.props.airport.id) {
      data.id = this.props.airport.id;
    }

    this.props.onSubmit(data);
  };

  handleCancel = () => {
    this.props.onCancel();
  };

  initMap = () => {
    return new google.maps.Map(document.getElementById("map"), {
      center: { lat: -34.397, lng: 150.644 },
      zoom: 8,
    });
  }

  render () {
    const map = this.initMap();
    const {airport} = this.props;
    return (
      <Formik
        initialValues={{
          name: airport && airport.name ? airport.name : '',
          country: airport && airport.country ? airport.country : '',
          location: airport && airport.location ? airport.location : '',
          departingFrom: airport && airport.departingFrom ? airport.departingFrom : '',
          landingTo: airport && airport.landingTo ? airport.landingTo : '',
        }}
        onSubmit={(values, {setSubmitting}) => {
          this.handleSubmit(values);
        }}
      >
        {({isSubmitting}) => (
          <Form>
            <label htmlFor="name">Name</label>
            <Field id="name" name="name" placeholder="Name" />
            <br />
            <label htmlFor="country">Country</label>
            <Field id="country" name="country" placeholder="Country" />
            <br />
            <label htmlFor="location">Location</label>
            <Field id="location" name="location" placeholder="Location" />
            <br />
            <label htmlFor="departingFrom">Departing from</label>
            <Field
              id="departingFrom"
              name="departingFrom"
              placeholder="Departing from"
            />
            <br />
            <label htmlFor="landingTo">Landing to</label>
            <Field id="landingTo" name="landingTo" placeholder="Landing to" />
            <br />
            <div id='map'></div>
            <Button type="submit" color="primary" variant="contained">
              Submit
            </Button>
            <Button variant="contained" onClick={this.handleCancel}>
              Cancel
            </Button>
          </Form>
        )}
      </Formik>
    );
  }
}

export default AirportForm;
