import * as services from 'redux/services/services.js';

export const FLIGHTS_FETCH = 'FLIGHTS_FETCH';
export const FLIGHT_FETCH = 'FLIGHT_FETCH';
export const FLIGHT_CREATE = 'FLIGHT_CREATE';
export const FLIGHT_UPDATE = 'FLIGHT_UPDATE';
export const FLIGHT_DELETE = 'FLIGHT_DELETE';

export const fetchFlights = () => {
  return dispatch => {
    services.get('flights')
    .then(res => res.json())
    .then(flights => dispatch({
      type: FLIGHTS_FETCH,
      flights: flights
    }))
    .catch(err => console.log(err))
  }
}

export const fetchFlightById = id => {
  return dispatch => {
    services.get(`flight/${id}`)
    .then(res => res.json())
    .then(flight => dispatch({
      type: FLIGHT_FETCH,
      flight: flight
    }))
  }
}

export const createFlight = payload => {
  return dispatch => {
    services.post('flights', payload)
    .then(res => {
      dispatch({type: FLIGHT_CREATE})
      dispatch(fetchFlights());
    })
  }
}

export const updateFlight = (id, payload) => {
  return dispatch => {
    services.put(`flight/${id}`, payload)
    .then(res => {
      dispatch({type: FLIGHT_UPDATE})
      dispatch(fetchFlights());
    })
  }
}

export const deleteFlight = id => {
  return dispatch => {
    services.deleteItem(`flight/${id}`)
    .then(res => {
      dispatch({type: FLIGHT_DELETE});
      dispatch(fetchFlights());
    })
  }
}
