import * as services from 'redux/services/services.js';

export const AIRPORTS_FETCH = 'AIRPORTS_FETCH';
export const AIRPORT_FETCH = 'AIRPORT_FETCH';
export const AIRPORT_CREATE = 'AIRPORT_CREATE';
export const AIRPORT_UPDATE = 'AIRPORT_UPDATE';
export const AIRPORT_DELETE = 'AIRPORT_DELETE';

export const fetchFlights = () => {
  return dispatch => {
    services.get('flights')
    .then(res => res.json())
    .then(flights => dispatch({
      type: AIRPORTS_FETCH,
      flights: flights
    }))
    .catch(err => console.log(err))
  }
}

export const fetchFlightById = id => {
  return dispatch => {
    services.get(`flight/${id}`)
    .then(res => res.json())
    .then(flight => dispatch({
      type: AIRPORT_FETCH,
      flight: flight
    }))
  }
}

export const createFlight = payload => {
  return dispatch => {
    services.post('flights', payload)
    .then(res => {
      dispatch({type: AIRPORT_CREATE})
      dispatch(fetchFlights());
    })
  }
}

export const updateFlight = (id, payload) => {
  return dispatch => {
    services.put(`flight/${id}`, payload)
    .then(res => {
      dispatch({type: AIRPORT_UPDATE})
      dispatch(fetchFlights());
    })
  }
}

export const deleteFlight = id => {
  return dispatch => {
    services.deleteItem(`flight/${id}`)
    .then(res => {
      dispatch({type: AIRPORT_DELETE});
      dispatch(fetchFlights());
    })
  }
}
