import React from 'react';
import {connect} from 'react-redux';
import {Modal, Button} from '@material-ui/core';
import {fetchFlights, deleteFlight} from 'redux/actions/flights.js';
import FlightList from 'components/flights/list/FlightList';
import ComponentListHeader from 'components/common/ComponentListHeader';
import FlightCreateContainer from 'components/flights/create/FlightCreateContainer';
import FlightUpdateContainer from 'components/flights/update/FlightUpdateContainer';
import 'assets/style/modalView.css';

class FlightListContainer extends React.Component {
  constructor (props) {
    super (props);

    this.state = {
      modalVisible: false,
      body: '',
    };
  }

  componentDidMount() {
    this.fetchFlights();
  }

  componentDidUpdate(prevProps, prevState) {
    const {flights} = this.props;
    const {flights: prevFlights} = prevProps;
    if (JSON.stringify(flights) !== JSON.stringify(prevFlights)) {
      this.fetchFlights();
    }
  }

  fetchFlights = () => {
    this.props.fetchFlights();
  };

  handleDeleteFlight = id => {
    this.props.deleteFlight (id);
    this.handleCloseModal();
  };

  handleOpenCreateModal = () => {
    const body = (
      <FlightCreateContainer onCancel={this.handleCloseModal}/>
    );

    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleOpenEditModal = (id) => {
    const body = (
      <FlightUpdateContainer
        flightId={id}
        onCancel={this.handleCloseModal}
      />
    );
    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleOpenDelete = id => {
    const body = (
      <React.Fragment>
        <p>Are you sure you want to delete this flight?</p>
        <Button
          color="secondary"
          variant="contained"
          onClick={() => this.handleDeleteFlight (id)}
        >
          Confirm
        </Button>
        <Button onClick={this.handleCloseModal}>Cancel</Button>
      </React.Fragment>
    );
    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleCloseModal = () => {
    this.setState({
      modalVisible: false,
      body: '',
    });
  };

  render() {
    const {flights} = this.props;
    const {modalVisible, body} = this.state;
    return (
      <React.Fragment>
        <ComponentListHeader onOpenCreateModal={this.handleOpenCreateModal} />
        <br />
        {flights && flights.length > 0
          ? <FlightList
              flights={flights || []}
              onEditClick={this.handleOpenEditModal}
              onDeleteClick={this.handleOpenDelete}
            />
          : 'No data to display'}
        {modalVisible &&
          <Modal
            className="twoesys-modalView__modal"
            open={true}
            onClose={this.handleCloseModal}
          >
            {body}
          </Modal>}
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
  flights: state.flightsReducer.flights,
});

const mapDispatchToProps = dispatch => {
  return {
    fetchFlights: () => dispatch (fetchFlights()),
    deleteFlight: id => dispatch (deleteFlight(id)),
  };
};

export default connect (mapStateToProps, mapDispatchToProps)(FlightListContainer);
