import React from 'react'
import { useFormik, Formik, Form, Field, ErrorMessage } from 'formik';
import {TextField, Button} from '@material-ui/core';

class AirportForm extends React.Component {
  handleSubmit = (data) => {
    console.log('data: ', data)
    this.props.onSubmit(data);
  }

  handleCancel = () => {
    this.props.onCancel();
  }
  render() {



    const formik = useFormik({
      initialValues: {
        name: '',
        country: '',
        location: '',
        departingFrom: '',
        landingTo: ''
      },
      onSubmit: (values, {setSubmitting}) => {
        console.log(values)
        this.handleSubmit(values);
      }
    });

    return (
         <Form onSubmit={formik.handleSubmit}>
          <TextField placeholder='Name' name='name' id='name' value={values.name} fullWidth/><br/>
          <TextField placeholder='Country' name='country' id='country' value={values.country} fullWidth/><br/>
          <TextField placeholder='Location' name='location' id='location' value={values.location} fullWidth/><br/>
          <TextField placeholder='Departing from' name='departingFrom' id='departingFrom' value={values.departingFrom} fullWidth/><br/>
          <TextField placeholder='Landing to' name='landingTo' id='landingTo' value={values.landingTo} fullWidth/><br/>
          <Button type='submit' color='primary' variant='contained'>Submit</Button>
          <Button variant='contained' onClick={this.handleCancel}>Cancel</Button>


