import React from 'react';
import {connect} from 'react-redux';
import {Modal, Button} from '@material-ui/core';
import {
  fetchAirports,
  fetchAirportById,
  createAirport,
  updateAirport,
  deleteAirport,
} from 'redux/reducers/airports.js';
import AirportList from 'components/airports/list/AirportList';
import AirportListHeader from 'components/airports/view/AirportListHeader';
import AirportForm from 'components/airports/form/AirportForm';
import 'assets/style/airportView.css';

class AirportListContainer extends React.Component {
  constructor (props) {
    super (props);

    this.state = {
      modalVisible: false,
      body: '',
    };
  }

  componentDidMount () {
    this.fetchAirports ();
  }

  componentDidUpdate (prevProps, prevState) {
    if (prevProps.airports !== this.props.airports) {
      this.fetchAirports ();
    }
  }

  fetchAirports = () => {
    this.props.fetchAirports ();
  };

  createAirport = data => {
    this.props.createAirport (data);
    this.handleCloseModal ();
  };

  handleDeleteAirport = id => {
    this.props.deleteAirport (id);
    this.handleCloseModal ();
  };

  handleToggleCreateModal = () => {
    const body = (
      <AirportForm
        onSubmit={this.createAirport}
        onCancel={this.handleCloseModal}
      />
    );

    this.setState ({
      modalVisible: !this.state.modalVisible,
      body,
    });
  };

  handleCloseModal = () => {
    this.setState ({
      modalVisible: false,
      body: '',
    });
  };

  handleEditClick = (id) => {
    const airportToUpdate = this.props.airports.find(airport => parseInt(airport.id) === parseInt(id));
    const body = (
      <AirportForm
        airport={airportToUpdate}
        onSubmit={this.createAirport}
        onCancel={this.handleCloseModal}
      />
    );
    this.setState ({
      modalVisible: true,
      body,
    });
  };

  handleDeleteClick = id => {
    const body = (
      <React.Fragment>
        <p>Are you sure you want to delete this airport?</p>
        <Button
          color="danger"
          variant="contained"
          onClick={() => this.handleDeleteAirport (id)}
        >
          Confirm
        </Button>
        <Button onClick={this.handleCloseModal}>Cancel</Button>
      </React.Fragment>
    );
    this.setState ({
      modalVisible: true,
      body,
    });
  };

  render () {
    const {airports} = this.props;
    const {modalVisible, body} = this.state;
    return (
      <React.Fragment>
        <AirportListHeader onToggleCreateModal={this.handleToggleCreateModal} />
        <br />
        {airports && airports.length > 0
          ? <AirportList
              airports={airports || []}
              onEditClick={this.handleEditClick}
              onDeleteClick={this.handleDeleteClick}
            />
          : 'No data to display'}
        {modalVisible &&
          <Modal
            className="twoesys-airportListView__modal"
            open={true}
            onClose={this.handleCloseModal}
          >
            {body}
          </Modal>}
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
  airports: state.airportsReducer.airports,
});

const mapDispatchToProps = dispatch => {
  return {
    fetchAirports: () => dispatch (fetchAirports ()),
    fetchAirportById: id => dispatch (fetchAirportById (id)),
    createAirport: data => dispatch (createAirport (data)),
    updateAirport: data => dispatch (updateAirport (data)),
    deleteAirport: id => dispatch (deleteAirport (id)),
  };
};

export default connect (mapStateToProps, mapDispatchToProps) (
  AirportListContainer
);
