import React from 'react';
import {connect} from 'react-redux';
import {Modal, Button} from '@material-ui/core';
import {fetchCountries, createCountry, updateCountry, deleteCountry} from 'redux/reducers/countries.js';
import CountryList from 'components/countries/list/CountryList';
import ComponentListHeader from 'components/common/ComponentListHeader';
import CountryForm from 'components/countries/form/CountryForm';
import 'assets/style/modalView.css';

class CountryListContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      modalVisible: false,
      body: '',
    }
  }

  componentDidMount() {
    this.fetchCountries();
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps.countries !== this.props.countries) {
      this.fetchCountries();
    }
  }

  fetchCountries = () => {
    this.props.fetchCountries();
  }

  handleCreateCountry = data => {
    this.props.createCountry(data);
    this.handleCloseModal();
  }

  handleUpdateCountry = data => {
    this.props.updateCountry(data);
    this.handleCloseModal();
  }

  handleDeleteCountry = id => {
    this.props.deleteCountry(id);
    this.handleCloseModal();
  };

  handleOpenCreateModal = () => {
    const body = (
      <CountryForm
        onSubmit={this.handleCreateCountry}
        onCancel={this.handleCloseModal}
      />
    );

    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleEditClick = (id) => {
    const countryToUpdate = this.props.countries.find(country => parseInt(country.id) === parseInt(id));
    const body = (
      <CountryForm
        country={countryToUpdate}
        onSubmit={this.handleUpdateCountry}
        onCancel={this.handleCloseModal}
      />
    );
    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleDeleteClick = id => {
    const body = (
      <React.Fragment>
        <p>Are you sure you want to delete this country?</p>
        <Button
          color="danger"
          variant="contained"
          onClick={() => this.handleDeleteCountry(id)}
        >
          Confirm
        </Button>
        <Button onClick={this.handleCloseModal}>Cancel</Button>
      </React.Fragment>
    );
    this.setState({
      modalVisible: true,
      body,
    });
  };

  handleCloseModal = () => {
    this.setState({
      modalVisible: false,
      body: '',
    });
  };

  render() {
    const {modalVisible, body} = this.state;
    const {countries} = this.props;
    return (
      <React.Fragment>
        <ComponentListHeader onOpenCreateModal={this.handleOpenCreateModal} />
        <br/>
        {countries && countries.length > 0
          ? <CountryList
              countries={countries || []}
              onEditClick={this.handleEditClick}
              onDeleteClick={this.handleDeleteClick}
            />
          : 'No data to display'}
        {modalVisible && 
          <Modal className='twoesys-modalView__modal' open={true} onClose={this.handleCloseModal}>
            {body}
          </Modal>
        }
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state, ownProps) => ({
  countries: state.countriesReducer.countries,
});

const mapDispatchToProps = (dispatch) => {
  return {
    fetchCountries: () => dispatch(fetchCountries()),
    createCountry: data => dispatch(createCountry(data)),
    updateCountry: data => dispatch (updateCountry(data)),
    deleteCountry: id => dispatch (deleteCountry(id)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CountryListContainer);
